fs = require('fs')
module.exports = {
	minify : function(){
	var path = arguments[0];	
	fs.readFile(path, 'utf8', function (err,data) {
	if (err) {
	return console.log(err);
	}else{
		var data = data.replace(/[\r\n\s]/g, '');
		var split_data = path.split(".");
		var pop_data = split_data.pop();
		var name = split_data.join('') + ".min."+pop_data;
		fs.writeFile(name, data, (err) => {
 		 if (err) throw err;
  			console.log('It\'s minified !');
		});
	}
	});

	}
}